#include <iostream>
#include <string>
#include <unordered_map>
using namespace std;

/*
* 给定两个字符串 s 和 t ，它们只包含小写字母。
* 字符串 t 由字符串 s 随机重排，然后在随机位置添加一个字母。
* 请找出在 t 中被添加的字母。
*/

//可以直接用find找，但是可以想想别的办法

//过了，完成度很低，得看答案

class Solution {
public:
	char findTheDifference(string s, string t) {
		//张安驰nb！！
		int sum1{};
		int sum2{};
		for (char c : s)
		{
			sum1 += c;
		}
		for (char c : t)
		{
			sum2 += c;
		}

		return sum2 - sum1;
	}
};


int main()
{
	cout << "hello world" << endl;

	string s = "";
	string t = "";
	char res = Solution().findTheDifference(s, t);
	cout << res << endl;

	system("pause");
	return 0;
}

